const calculator = () => {
  const startDate = new Date(document.getElementById("booking_start_date"));
  const endDate = new Date(document.getElementById("booking_end_date"));
  const bookingDays = document.getElementById("booking_days");
  const subtotal = document.getElementById("subtotal");
  // const price = document.getElementById("")
  startDate.addEventListener("change", () => {
      const ONE_DAY = 1000 * 60 * 60 * 24;
      const differenceMs = Math.abs(endDate.value - startDate.value);
      const days = Math.round(differenceMs / ONE_DAY);
      console.log(days);
      bookingDays.innerHTML = `${days}`
  });
  endDate.addEventListener("change", () => {
    if (endDate > startDate) {
      console.log(startDate);
      const days = endDate.value - startDate.value
      console.log(days);
    }
  });
}
export default calculator;
